from queue import Queue
from node import node
class automato():
    def __init__(self, conjunto, alfabeto, q_inicial, conjunto_final):
        self.conjunto = conjunto
        self.alfabeto = alfabeto
        self.q_inicial = q_inicial
        self.conjunto_final = conjunto_final
        self.start = node(None, 's')
        self.ultimo_adiciondo = node(None, 'f')

    def create(self):
        # Passo 1: Criar automaato para validar
        elemento = 0
        while(True):
            if(self.conjunto[elemento]==self.q_inicial):
                novo_no = node(self.conjunto[elemento], self.alfabeto[elemento], start=True)
                proximo = node(self.conjunto[elemento+1], self.alfabeto[elemento+1])
                novo_no.set_proximo(proximo)
                self.start.set_proximo(novo_no)
                self.ultimo_adiciondo = proximo

            else:
                proximo = node(self.conjunto[elemento+1], self.alfabeto[elemento+1])
                self.ultimo_adiciondo.set_proximo(proximo)
                self.ultimo_adiciondo = proximo
            
            elemento += 1
            if(elemento == len(self.conjunto)-1):
                break
        no_validacao = node('V', 'F')
        self.ultimo_adiciondo.set_proximo(no_validacao)
    
    
    def transicao(self,no_atual, valor, first=False):
        # funcao de transicao
        if(no_atual.valor == valor and first):
            return no_atual.get_proximo()
        elif(no_atual.valor == valor):
            return no_atual.get_proximo() 
        else:
            return None
            

    def run(self, sequencia):
        tamanho = sequencia.__len__()
        elemento = 0
        Fila = Queue()
        Fila.put(self.start.get_proximo())
        num_sequencias = 0
        while(elemento<tamanho):
            valor = sequencia[elemento]
            tamanho_fila = Fila.qsize()
            i = 0
            for i in range(tamanho_fila):
                no_atual = Fila.get()
                if(no_atual.elemento == 'V' and no_atual.valor == 'F'):
                    num_sequencias+=1
                    continue
                if(no_atual.elemento == 0):
                    proximo   = self.transicao(no_atual, valor, first=True)
                    if(proximo != None):
                        Fila.put(no_atual)
                        Fila.put(proximo)
                    else:
                        Fila.put(no_atual)

                else:
                    proximo = self.transicao(no_atual, valor)
                    if(proximo != None):
                        Fila.put(proximo)
            elemento += 1 
        return num_sequencias




            


            
